import React, { useState, useEffect } from 'react';
import { Settings, Mail, Trash2, Save, Eye, EyeOff } from 'lucide-react';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const SettingsPage = ({ user, onUserUpdate, onLogout }) => {
  const [alternativeEmail, setAlternativeEmail] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (user?.alternativeEmail) {
      setAlternativeEmail(user.alternativeEmail);
    }
  }, [user]);

  const handleSaveAlternativeEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await axios.put(`${API_BASE_URL}/api/user/alternative-email`, {
        alternativeEmail,
        currentPassword
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setSuccess('Alternative E-Mail wurde gespeichert!');
      onUserUpdate(response.data.user);
      setCurrentPassword('');
    } catch (err) {
      setError(err.response?.data?.message || 'Fehler beim Speichern');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteAccount = async () => {
    const confirmed = window.confirm(
      'Bist du sicher, dass du deinen Account löschen möchtest? Diese Aktion kann nicht rückgängig gemacht werden!'
    );

    if (!confirmed) return;

    const passwordConfirmed = window.prompt(
      'Bitte gib dein Passwort ein, um die Löschung zu bestätigen:'
    );

    if (!passwordConfirmed) return;

    setLoading(true);
    setError('');

    try {
      await axios.delete(`${API_BASE_URL}/api/user/delete-account`, {
        data: { password: passwordConfirmed }
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      localStorage.removeItem('token');
      localStorage.removeItem('user');
      onLogout();
    } catch (err) {
      setError(err.response?.data?.message || 'Fehler beim Löschen des Accounts');
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-purple-50 p-4">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl p-8">
          <div className="flex items-center mb-8">
            <Settings className="w-8 h-8 text-pink-500 mr-3" />
            <h1 className="text-3xl font-bold text-gray-800">Einstellungen</h1>
          </div>

          {/* Alternative Email Section */}
          <div className="mb-8 p-6 bg-gray-50 rounded-lg">
            <div className="flex items-center mb-4">
              <Mail className="w-5 h-5 text-gray-600 mr-2" />
              <h2 className="text-xl font-semibold text-gray-800">Alternative E-Mail</h2>
            </div>
            
            <p className="text-gray-600 mb-4">
              Hier kannst du eine alternative E-Mail-Adresse für die Passwort-Wiederherstellung festlegen. 
              Diese wird nur verwendet, falls du den Zugriff auf deine primäre E-Mail oder Telefonnummer verlierst.
            </p>

            <form onSubmit={handleSaveAlternativeEmail} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Alternative E-Mail-Adresse
                </label>
                <input
                  type="email"
                  value={alternativeEmail}
                  onChange={(e) => setAlternativeEmail(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
                  placeholder="alternative@example.com"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Aktuelles Passwort bestätigen
                </label>
                <div className="relative">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                    className="w-full px-4 py-2 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
                    placeholder="••••••••"
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-2.5 text-gray-400 hover:text-gray-600"
                  >
                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                  </button>
                </div>
              </div>

              <button
                type="submit"
                disabled={loading}
                className="flex items-center px-6 py-2 bg-pink-500 text-white rounded-lg hover:bg-pink-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <Save className="w-4 h-4 mr-2" />
                {loading ? 'Speichern...' : 'Speichern'}
              </button>
            </form>

            {error && (
              <div className="mt-4 p-3 bg-red-50 text-red-600 rounded-lg text-sm">
                {error}
              </div>
            )}

            {success && (
              <div className="mt-4 p-3 bg-green-50 text-green-600 rounded-lg text-sm">
                {success}
              </div>
            )}
          </div>

          {/* Account Deletion Section */}
          <div className="p-6 bg-red-50 rounded-lg">
            <div className="flex items-center mb-4">
              <Trash2 className="w-5 h-5 text-red-600 mr-2" />
              <h2 className="text-xl font-semibold text-red-800">Account löschen</h2>
            </div>
            
            <p className="text-red-700 mb-4">
              Wenn du deinen Account löschst, werden alle deine Daten permanent entfernt. 
              Diese Aktion kann nicht rückgängig gemacht werden.
            </p>

            <button
              onClick={handleDeleteAccount}
              disabled={loading}
              className="flex items-center px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              <Trash2 className="w-4 h-4 mr-2" />
              {loading ? 'Löschen...' : 'Account permanent löschen'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPage;
