import React, { useState, useEffect, useRef } from 'react';
import { Send, ArrowLeft, Search, User } from 'lucide-react';
import axios from 'axios';
import io from 'socket.io-client';

const Chat = ({ user, onBack, initialChatUser }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [chatUser, setChatUser] = useState(initialChatUser || null);
  const [conversations, setConversations] = useState([]);
  const [socket, setSocket] = useState(null);
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const [showConversationList, setShowConversationList] = useState(!initialChatUser);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      if (user) {
        newSocket.emit('joinRoom', { userId: user.id });
      }
    });

    newSocket.on(`message_${user?.id}`, (message) => {
      if (chatUser && (message.sender._id === chatUser._id || message.receiver._id === chatUser._id)) {
        setMessages(prev => [...prev, message]);
      }
    });

    return () => newSocket.close();
  }, [user, chatUser]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (user && !initialChatUser) {
      fetchConversations();
    }
  }, [user]);

  useEffect(() => {
    if (chatUser) {
      fetchMessages();
      setShowConversationList(false);
    }
  }, [chatUser]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchConversations = async () => {
    try {
      const token = localStorage.getItem('token');
      // In a real app, you'd have an endpoint to get conversations
      // For now, we'll use the search endpoint to get all users
      const response = await axios.get('http://localhost:5000/api/search', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setConversations(response.data.slice(0, 10)); // Limit to first 10 for demo
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const fetchMessages = async () => {
    if (!chatUser) return;
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/messages/${chatUser._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    } finally {
      setLoading(false);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !chatUser || !socket) return;

    const messageData = {
      receiverId: chatUser._id,
      content: newMessage.trim()
    };

    try {
      socket.emit('sendMessage', messageData);
      setNewMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleUserSelect = (selectedUser) => {
    setChatUser(selectedUser);
  };

  const handleBackToList = () => {
    setShowConversationList(true);
    setChatUser(null);
    setMessages([]);
  };

  if (showConversationList) {
    return (
      <div className="max-w-4xl mx-auto p-6">
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
          <div className="bg-gradient-to-r from-pink-500 to-red-500 p-6">
            <div className="flex items-center justify-between">
              <h2 className="text-2xl font-bold text-white">Messages</h2>
              {onBack && (
                <button
                  onClick={onBack}
                  className="bg-white text-primary-600 px-4 py-2 rounded-lg font-medium hover:bg-gray-100 transition-colors"
                >
                  Back
                </button>
              )}
            </div>
          </div>

          <div className="p-4">
            <div className="mb-4">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <input
                  type="text"
                  placeholder="Search conversations..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
              </div>
            </div>

            <div className="space-y-2">
              {conversations.map((conversation) => (
                <div
                  key={conversation._id}
                  onClick={() => handleUserSelect(conversation)}
                  className="flex items-center gap-3 p-3 rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                >
                  <div className="w-12 h-12 bg-gray-200 rounded-full flex items-center justify-center flex-shrink-0">
                    {conversation.profile.photos && conversation.profile.photos.length > 0 ? (
                      <img
                        src={conversation.profile.photos[0]}
                        alt={conversation.profile.name}
                        className="w-full h-full rounded-full object-cover"
                      />
                    ) : (
                      <User className="w-6 h-6 text-gray-400" />
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="font-semibold text-gray-900 truncate">{conversation.profile.name}</h3>
                    <p className="text-sm text-gray-600 truncate">
                      {conversation.profile.bio || 'Start a conversation...'}
                    </p>
                  </div>
                  <div className="text-xs text-gray-500">
                    {conversation.profile.location}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
        <div className="bg-gradient-to-r from-pink-500 to-red-500 p-6">
          <div className="flex items-center gap-4">
            <button
              onClick={handleBackToList}
              className="text-white hover:bg-white hover:bg-opacity-20 p-2 rounded-lg transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
            </button>
            <div className="flex items-center gap-3 flex-1">
              <div className="w-10 h-10 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                {chatUser?.profile.photos && chatUser.profile.photos.length > 0 ? (
                  <img
                    src={chatUser.profile.photos[0]}
                    alt={chatUser.profile.name}
                    className="w-full h-full rounded-full object-cover"
                  />
                ) : (
                  <User className="w-5 h-5 text-white" />
                )}
              </div>
              <div>
                <h3 className="font-semibold text-white">{chatUser?.profile.name}</h3>
                <p className="text-sm text-white text-opacity-90">
                  {chatUser?.profile.age} years old â€¢ {chatUser?.profile.location}
                </p>
              </div>
            </div>
          </div>
        </div>

        <div className="h-96 overflow-y-auto p-6 bg-gray-50">
          {loading ? (
            <div className="text-center py-8">
              <div className="inline-block animate-spin rounded-full h-6 w-6 border-b-2 border-primary-500"></div>
            </div>
          ) : messages.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500">No messages yet. Start the conversation!</p>
            </div>
          ) : (
            <div className="space-y-4">
              {messages.map((message) => (
                <div
                  key={message._id}
                  className={`flex ${message.sender._id === user.id ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-xs px-4 py-2 rounded-2xl ${
                      message.sender._id === user.id
                        ? 'bg-primary-500 text-white'
                        : 'bg-white text-gray-900 shadow'
                    }`}
                  >
                    <p className="text-sm">{message.content}</p>
                    <p className={`text-xs mt-1 ${
                      message.sender._id === user.id ? 'text-white text-opacity-70' : 'text-gray-500'
                    }`}>
                      {new Date(message.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </p>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
          )}
        </div>

        <form onSubmit={sendMessage} className="p-4 bg-white border-t">
          <div className="flex gap-2">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type a message..."
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            />
            <button
              type="submit"
              disabled={!newMessage.trim()}
              className="bg-primary-500 text-white p-2 rounded-lg hover:bg-primary-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Send className="w-5 h-5" />
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Chat;
