import React, { useState } from 'react';
import { Camera, X } from 'lucide-react';

const ImageUpload = ({ images, onImagesChange, maxImages = 5 }) => {
  const [uploading, setUploading] = useState(false);

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    
    if (images.length + files.length > maxImages) {
      alert(`Maximum ${maxImages} images allowed`);
      return;
    }

    setUploading(true);
    
    files.forEach(file => {
      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = (event) => {
          const imageUrl = event.target.result;
          onImagesChange([...images, imageUrl]);
          setUploading(false);
        };
        reader.readAsDataURL(file);
      }
    });
  };

  const removeImage = (index) => {
    const newImages = images.filter((_, i) => i !== index);
    onImagesChange(newImages);
  };

  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {images.map((image, index) => (
          <div key={index} className="relative group">
            <div className="aspect-square bg-gray-200 rounded-lg overflow-hidden">
              <img
                src={image}
                alt={`Profile ${index + 1}`}
                className="w-full h-full object-cover"
              />
            </div>
            <button
              onClick={() => removeImage(index)}
              className="absolute top-2 right-2 bg-red-500 text-white p-1 rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
            >
              <X className="w-4 h-4" />
            </button>
            {index === 0 && (
              <div className="absolute bottom-2 left-2 bg-primary-500 text-white px-2 py-1 rounded text-xs">
                Main Photo
              </div>
            )}
          </div>
        ))}
        
        {images.length < maxImages && (
          <label className="aspect-square bg-gray-100 border-2 border-dashed border-gray-300 rounded-lg flex flex-col items-center justify-center cursor-pointer hover:bg-gray-50 transition-colors">
            <Camera className="w-8 h-8 text-gray-400 mb-2" />
            <span className="text-sm text-gray-600">Add Photo</span>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
            />
          </label>
        )}
      </div>
      
      {uploading && (
        <div className="text-center text-sm text-gray-600">
          Uploading images...
        </div>
      )}
      
      <p className="text-xs text-gray-500">
        Upload up to {maxImages} photos. First photo will be your main profile picture.
      </p>
    </div>
  );
};

export default ImageUpload;
