import React, { useState } from 'react';
import { Phone, MessageCircle } from 'lucide-react';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const PhoneAuth = ({ onAuth }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [phoneNumber, setPhoneNumber] = useState('');
  const [pin, setPin] = useState('');
  const [showPin, setShowPin] = useState(false);
  const [generatedPin, setGeneratedPin] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    profile: {
      name: '',
      age: '',
      gender: 'male',
      bio: '',
      interests: '',
      
      // KÃ¶rperliche Merkmale
      height: '',
      weight: '',
      bodyType: 'average',
      hairColor: '',
      hairStyle: '',
      eyeColor: '',
      
      // PersÃ¶nliche Merkmale
      ethnicity: '',
      religion: '',
      languages: [],
      maritalStatus: 'single',
      hasChildren: false,
      wantsChildren: false,
      smoking: 'never',
      drinking: 'occasionally',
      
      // Beruf & Bildung
      profession: '',
      education: '',
      
      // Standort
      country: '',
      city: '',
      
      // Medien
      photos: []
    }
  });
  
  const handlePhoneSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Login with phone number - show PIN input
        setShowPin(true);
      } else {
        // Register with phone number and PIN
        const response = await axios.post(`${API_BASE_URL}/api/register-phone-pin`, {
          phoneNumber,
          profile: formData.profile
        });
        
        setGeneratedPin(response.data.pin);
        setShowPin(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handlePinSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Login with PIN
        const response = await axios.post(`${API_BASE_URL}/api/login-phone-pin`, {
          phoneNumber,
          pin
        });
        
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        onAuth(response.data.user);
      } else {
        // Registration complete - login with generated PIN
        const response = await axios.post(`${API_BASE_URL}/api/login-phone-pin`, {
          phoneNumber,
          pin: generatedPin
        });
        
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        onAuth(response.data.user);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Verification failed');
    } finally {
      setLoading(false);
    }
  };

  const handleProfileChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      profile: {
        ...prev.profile,
        [field]: value
      }
    }));
  };

  if (showPin) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-purple-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
          <div className="text-center mb-8">
            <MessageCircle className="w-16 h-16 text-pink-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-gray-800">
              {isLogin ? 'Enter PIN' : 'Your PIN'}
            </h2>
            <p className="text-gray-600 mt-2">
              {isLogin 
                ? `Enter your 4-digit PIN for ${phoneNumber}` 
                : `Your PIN is: ${generatedPin}`
              }
            </p>
            {!isLogin && generatedPin && (
              <div className="mt-4 p-3 bg-green-50 border border-green-200 rounded-lg">
                <p className="text-green-800 font-bold text-lg">{generatedPin}</p>
                <p className="text-green-600 text-sm mt-1">Save this PIN for future logins</p>
              </div>
            )}
          </div>

          <form onSubmit={handlePinSubmit}>
            <div className="mb-6">
              <div className="flex justify-center space-x-2">
                {[0, 1, 2, 3].map((index) => (
                  <input
                    key={index}
                    type="text"
                    maxLength="1"
                    className="w-16 h-16 text-center text-2xl border-2 border-gray-300 rounded-lg focus:border-pink-500 focus:outline-none font-bold"
                    value={pin[index] || ''}
                    onChange={(e) => {
                      const newPin = pin.split('');
                      newPin[index] = e.target.value;
                      setPin(newPin.join(''));
                      // Auto-focus next input
                      if (e.target.value && index < 3) {
                        const nextInput = e.target.parentElement.children[index + 1];
                        if (nextInput) nextInput.focus();
                      }
                    }}
                    onKeyDown={(e) => {
                      // Handle backspace
                      if (e.key === 'Backspace' && !pin[index] && index > 0) {
                        const prevInput = e.target.parentElement.children[index - 1];
                        if (prevInput) prevInput.focus();
                      }
                    }}
                  />
                ))}
              </div>
            </div>

            {error && (
              <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-lg text-sm">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={loading || pin.length !== 4}
              className="w-full bg-pink-500 text-white py-3 rounded-lg hover:bg-pink-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors font-semibold"
            >
              {loading ? 'Verifying...' : (isLogin ? 'ðŸ”“ Login' : 'âœ… Complete Registration')}
            </button>

            <button
              type="button"
              onClick={() => setShowPin(false)}
              className="w-full mt-3 text-gray-600 hover:text-gray-800 transition-colors"
            >
              Back
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-purple-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <Phone className="w-16 h-16 text-pink-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-800">
            {isLogin ? 'Login with Phone' : 'Register with Phone'}
          </h2>
          <p className="text-gray-600 mt-2">
            {isLogin ? 'Enter your phone number' : 'Create account with phone number'}
          </p>
        </div>

        <form onSubmit={handlePhoneSubmit}>
          <div className="mb-4">
            <div className="relative">
              <Phone className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <input
                type="tel"
                placeholder="+49 123 4567890"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                className="w-full pl-10 pr-3 py-3 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                required
              />
            </div>
          </div>

          {/* reCAPTCHA container for Firebase */}
          <div id="recaptcha-container"></div>

          {!isLogin && (
            <div className="space-y-4 mb-6">
              <div>
                <input
                  type="text"
                  placeholder="Name"
                  value={formData.profile.name}
                  onChange={(e) => handleProfileChange('name', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                  required
                />
              </div>
              
              <div className="grid grid-cols-2 gap-4">
                <input
                  type="number"
                  placeholder="Age"
                  value={formData.profile.age}
                  onChange={(e) => handleProfileChange('age', e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                  required
                />
                
                <select
                  value={formData.profile.gender}
                  onChange={(e) => handleProfileChange('gender', e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                >
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </select>
              </div>

              <textarea
                placeholder="Bio"
                value={formData.profile.bio}
                onChange={(e) => handleProfileChange('bio', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                rows="3"
              />
            </div>
          )}

          {error && (
            <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-lg text-sm">
              {error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-pink-500 text-white py-4 rounded-lg hover:bg-pink-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors font-semibold text-lg shadow-lg"
          >
            {loading ? 'Processing...' : (isLogin ? 'ðŸ“± SEND VERIFICATION CODE' : 'ðŸ‘¤ CREATE ACCOUNT')}
          </button>

          <button
            type="button"
            onClick={() => setIsLogin(!isLogin)}
            className="w-full mt-4 bg-gray-100 text-gray-700 py-3 rounded-lg hover:bg-gray-200 transition-colors font-medium"
          >
            {isLogin ? 'ðŸ”” Need an account? Register' : 'ðŸ”‘ Have an account? Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default PhoneAuth;
